import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setSearchTerm, clearSearchTerm, selectSearchTerm, loadAutocomplete, isLoading, selectAutocompleteList } from '../../../features/search/searchSlice.js';
import "./Search.css"
import searchIconUrl from "../../../media/search.png";
import clearIconUrl from "../../../media/close.png";
import { useParams } from 'react-router-dom';



export const Search = () => {

    const searchTerm = useSelector(selectSearchTerm);
    const dispatch = useDispatch();
  const autocompletePreview = useSelector(selectAutocompleteList);
  const isLoadingText = useSelector(isLoading);
  
  useEffect(() => {
    dispatch(loadAutocomplete({text: searchTerm}));
  }, [dispatch, searchTerm]);

  if (isLoadingText) {
    return (<div id="loading-container">
        <p id="loading">loading suggestions...</p>
      </div>);
  }

    const onSearchTermChangeHandler = (e) => {
        const userInput = e.target.value;
        dispatch(setSearchTerm(userInput));
        
    };

    const onClearSearchTermHandler = () => {
        dispatch(clearSearchTerm());
    };

    return (
        <div id="search">
            <div id="search-container">
                <img id="search-icon" alt="search" src={searchIconUrl} />
                <input
                    id="search"
                    type="text"
                    value={searchTerm}
                    onChange={onSearchTermChangeHandler}
                    placeholder="Search me"
                />
                {searchTerm.length > 0 && (
                    <button
                        onClick={onClearSearchTermHandler}
                        type="button"
                        id="search-clear-button"
                    >
                        <img src={clearIconUrl} alt="X" id='search-clear-icon' />
                    </button>
                )}
            </div>
            <div id="autocomplete-container">

            </div>
        </div>
    );

}
