export const formatDate = (date) => {
    const formattedDate = date.slice(0, 19);
    const currentTime = new Date(formattedDate);
    return currentTime;
};

export const chooseBackground = (weatherNum) => {
    switch (weatherNum) {
        case weatherNum >= 1 && weatherNum <= 2 :
            break;
        default:
            break;
    }
}
