import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
//import { formatDate } from '../../../helperFunctions/helpers';
import { addFavorite, selectFavoriteCity, selectAllFavorites } from '../../features/favorites/favoritesSlice';
import { formatDate } from '../../helperFunctions/helpers';
import './favorites.css';

export const Favorites = () => {

    const favorites = useSelector(selectAllFavorites);
    //const dispatch = useDispatch();

    // console.log(cityName);

    // const onClickHandler = () => {
    //     const favoriteObj = {
    //         key: cityKey,
    //         name: cityName,
    //         current: currentCondition
    //     }
    //     dispatch(addFavorite(favoriteObj))
    // }

    // const formattedDate = formatDate(currentCondition.LocalObservationDateTime);
    // const currentHour = formattedDate.getHours();
    // const currentMinutes = formattedDate.getMinutes();

    return (
        <div id="favorites-container">
            <div id="white-overlay">
                <h2 id="fav-header">Your Favorites</h2>
                <div className="general-flex-container">
                    {favorites.map((favorite, i) => (
                        <div className="general-box fav-box" key={i}>
                            <h3 className="fav-name">{favorite.name}</h3>
                            <p>{favorite.current.Temperature.Metric.Value} {favorite.current.Temperature.Metric.Unit}</p>
                            <p>{formatDate(favorite.current.LocalObservationDateTime).getHours()}:
                                {formatDate(favorite.current.LocalObservationDateTime).getMinutes()}</p>
                            <button id='remove-fav'>Remove</button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );


}
