import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { selectCurrentCityName, setCurrentCityKey, clearCurrentCityKey, selectCurrentCityKey, selectCurrentCondition, isLoading, loadCurrentCondition } from '../../../features/currentCity/correntCitySlice';
import { useParams } from 'react-router-dom';



export const WeatherPreview = () => {

    const cityKey = useSelector(selectCurrentCityKey);
    const cityName = useSelector(selectCurrentCityName);
    const currentCondition = useSelector(selectCurrentCondition);
    const dispatch = useDispatch();
    const isLoadingCurrentCondition = useSelector(isLoading);

    const getCurrentInfo = (currentCondition) => {

        if (currentCondition) {

        
        weatherDescription = currentCondition.WeatherText;
        celciuce = currentCondition.Temperature.Metric.Value;


        }

    }

    useEffect(() => {
        if (cityKey) {
            dispatch(loadCurrentCondition({ key: cityKey }));
        }
    }, [dispatch, cityKey]);

    if (isLoadingCurrentCondition) {
        return (<div id="loading-container">
            <p id="loading">loading current condition...</p>
        </div>);
    }

    // const onSearchTermChangeHandler = (e) => {
    //     const userInput = e.target.value;
    //     dispatch(setSearchTerm(userInput));
    // };

    // const onClearSearchTermHandler = () => {
    //     dispatch(clearSearchTerm());
    // };
    //const currentTime = Date.parse(currentCondition.LocalObservationDateTime);
    if (currentCondition) {

        const currentTime = new Date(currentCondition.LocalObservationDateTime);
        const currentHour = currentTime.getHours();
        const currentMinutes = currentTime.getMinutes();

        return (
            <div id="current-condition">
                <h2>{cityName}</h2>
                <p>{weatherDescription}</p>
                <p>Current temperature is : {currentCondition ? currentCondition.Temperature.Metric.Value : ''} {currentCondition ? currentCondition.Temperature.Metric.Unit : ''}</p>
                <p>Current time is : {currentHour} : {currentMinutes}</p>   
            </div>
        );

    }       

}
