export const API_ROOT = 'http://dataservice.accuweather.com';

const API_KEY = "8kenz8patTO8I8TAP3fAp9RC5lzOGF7t";

export const getAutocomplete = async (payload) => {

  const url = `${API_ROOT}/locations/v1/cities/autocomplete`;
  const query = `?apikey=${API_KEY}&q=${payload.text}`;
  const response = await fetch(url + query);
  const responseJSON = await response.json();
  console.log(responseJSON);
  return responseJSON;

};

export const getCurrentCondition = async (payload) => {

  const cityKey = payload.key;
  const url = `${API_ROOT}/currentconditions/v1/`;
  const query = `${cityKey}?apikey=${API_KEY}`;
  const response = await fetch(url + query);
  const responseJSON = await response.json();
  console.log(responseJSON);
  return responseJSON;

};

export const getSubredditPosts = async (payload) => {
  const {subreddit, filter} = payload;
  const response = await fetch(`${API_ROOT}/${subreddit}/${filter}.json`);
  const json = await response.json();
  return json;
};

export const getSubreddits = async () => {
  const response = await fetch(`${API_ROOT}/subreddits.json`);
  const json = await response.json();

  return json;
};

