export const formatDate = (date) => {
    const formattedDate = date.slice(0, 19);
    const currentTime = new Date(formattedDate);
    return currentTime;
};

export const chooseClassName = (weatherNum) => {
    console.log( typeof weatherNum);
    switch (weatherNum) {
        case weatherNum >= 1 && weatherNum <= 2 :
            return "sunny";
        case weatherNum >= 3 && weatherNum <=6 :
            return "partly-sunny";
        case weatherNum >= 7 && weatherNum <= 11 :
            return "cloudy";
        case weatherNum >= 12 && weatherNum <= 21 :
            return "rainy";
        case weatherNum >= 22 && weatherNum <= 29 :
            return "snowy";
        default:
            return "night";
    }
};
