export const favoritesSlice = createSlice({
    name: 'favorites',
    initialState: {
    //favorites are saved by key - for easy access
      allFavorites: {},
    },
    reducers: {
        addFavorite(state, action) {
            const cityKey = action.payload.key;
            state.allFavorites[cityKey] = action.payload.cityInfo;
        },
        removeFavorite(state, action) {
            const cityKey = action.payload;
            delete state.allFavorites[cityKey];
        }
    }
});


//selectors
export const selectAllFavorites = (state) => state.favorites.allFavorites;
export const selectFavoriteCity = (state, cityKey) => state.search.allFavorites[cityKey];
//actions
export const {addFavorite, removeFavorite} = favoritesSlice.actions;
//reducer
export default favoritesSlice.reducer;
