import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { selectIsFavorite, setIsFavorite, selectCurrentCityName, selectCurrentCityKey, selectCurrentCondition, isLoadingCurrentCity, loadCurrentCondition } from '../../../features/currentCity/correntCitySlice';
import { formatDate, chooseClassName } from '../../../helperFunctions/helpers';
import { FiveDays } from './fiveDays/FiveDays';
import { addFavorite } from '../../../features/favorites/favoritesSlice';
// import "./weatherPreview.css";


export const WeatherPreview = () => {

    const cityKey = useSelector(selectCurrentCityKey);
    const cityName = useSelector(selectCurrentCityName);
    const currentCondition = useSelector(selectCurrentCondition);
    const isFavorite = useSelector(selectIsFavorite);
    const isLoadingCurrentCondition = useSelector(isLoadingCurrentCity);
    const dispatch = useDispatch();
    let addFavoriteElment;

    // console.log(cityName);

    useEffect(() => {
        if (cityKey) {
            dispatch(loadCurrentCondition({ key: cityKey }));
        }
    }, [dispatch, cityKey]);

    if (isLoadingCurrentCondition) {
        return (<div id="loading-container">
            <p id="loading">loading current condition...</p>
        </div>);
    }

    const onClickHandler = () => {
        const favoriteObj = {
            key: cityKey,
            name: cityName,
            current: currentCondition
        }
        dispatch(addFavorite(favoriteObj));
        dispatch(setIsFavorite(true));
    }

    // console.log(currentCondition);

    const formattedDate = formatDate(currentCondition.LocalObservationDateTime);
    const currentHour = formattedDate.getHours();
    const currentMinutes = formattedDate.getMinutes();

    if (isFavorite) {
        addFavoriteElment = <div id="favorite-button">it is favorite</div>;
    } else {
        addFavoriteElment = <div id="favorite-button" onClick={onClickHandler}>add to favorites</div>;
    }


    return (
        <>
            <div id="current-condition">
                <h2>{cityName}</h2>
                {addFavoriteElment}
                <p>{currentCondition.WeatherText}</p>
                <p>Current temperature is : {currentCondition.Temperature.Metric.Value} {currentCondition.Temperature.Metric.Unit}</p>
                <p>Current time is : {currentHour + ":" + currentMinutes}</p>   
            </div>
            <div id="five-days">
                <FiveDays />
            </div>
        </>
    );
    

}
