import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { loadFiveDays, isLoadingFiveDays, selectFiveDays } from '../../../features/fiveDays/fiveDaysSlice.js';
//import "./Search.css"
import { useParams } from 'react-router-dom';
import { setCurrentCityName, setCurrentCityKey, selectCurrentCityKey } from '../../../../features/currentCity/correntCitySlice';
import { formatDate } from '../../../helperFunctions/helpers';


export const FiveDays = () => {

    const cityKey = useSelector(selectCurrentCityKey);
    const fiveDaysObj = useSelector(selectFiveDays);
    const dispatch = useDispatch();
    const loadingFiveDays = useSelector(isLoadingFiveDays);

    useEffect(() => {
        dispatch(loadFiveDays({ key: cityKey }));
    }, [dispatch, cityKey]);

    if (loadingFiveDays) {
        return <div id="loading-container">
            <p id="loading">loading...</p>
        </div>;
    }

    const daysList = fiveDaysObj.DailyForecasts;

    //helper
    const getDayName = (date) => {
        const formattedDate = formatDate(date);
        const dayNumber = formattedDate.getDay();

        switch (dayNumber) {
            case 0:
                return 'Sunday';
            case 1:
                return 'Monday';
            case 2:
                return 'Tuesday';
            case 3:
                return 'Wednesday';
            case 4:
                return 'Thursday';
            case 5:
                return 'Friday';
            case 6:
                return 'Saturday';
            default:
                return '';
        }
    }

    return (
        <div id="five-days-container">
            
            {daysList.map((day) => (
                    <div>
                        <h3>{getDayName(day.Date)}</h3>
                        <p></p>
                    </div>
                ))}
            
        </div>
    );

}
